let successPayments = [];

for (const item of $input.all()) {
  // Assume item.json has the structure { payments: [...] }
  const payments = item.json.payments || [];

  for (const payment of payments) {
    if (payment.status === "succeeded") {
      successPayments.push(payment);
    }
  }
}


return { successPayments: successPayments};

function transformPaymentData(inputJson) {
  // Parse JSON string input
  const dataObj = (typeof inputJson === 'string')
    ? JSON.parse(inputJson)
    : inputJson;

  // Extract the payment intents array
  const paymentIntents = Array.isArray(dataObj) && dataObj.length > 0
    ? dataObj[0].data || []
    : dataObj.data || [];

  // Construct simplified payments array
  const payments = paymentIntents.map(intent => ({
    payment_intent_id: intent.id,
    amount: intent.amount,
    currency: intent.currency,
    status: intent.status,
    createdAt: new Date(intent.created * 1000),
  }));

  return { payments };
}

let payments = transformPaymentData($input.all()[0].json);

return payments;

