let tagId = "";

const category = $('Intent Classifier').all()[0].json.output.category;

for (const item of $('Get All Tags from Intercom').all()[0].json.data) {
  if (item.name === category) {
    tagId = item.id;
    break; // Exit loop once tagId is found
  }
}

return { tag: tagId };

